import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

import loginBg from "../assets/login-bg.jpg";
import "../App.css";

function Login({ login }) {
  const [username, setUsername] = useState(""); // username or email
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const navigate = useNavigate();
  const BASE = "http://localhost:5000";

  const handleLoginClick = async () => {
    if (username.trim() === "" || password.trim() === "") {
      alert("Please fill all fields");
      return;
    }

    try {
      setMsg("");

      // ✅ backend login (if backend works)
      await axios.post('${BASE}/api/auth/login', {
        email: username,      // backend usually expects email
        password: password,
      });

      // ✅ keep your old "login(username)" behavior if you use it
      if (login) login(username);

      // ✅ Admin redirect (course/demo)
      if (username === "72330526@students.liu.edu.lb" && password === "Rayan72330526") {
        navigate("/admin");
      } else {
        navigate("/");
      }
    } catch (err) {
      console.log(err);

      // ✅ still allow demo admin login even if backend fails
      if (username === "72330526@students.liu.edu.lb" && password === "Rayan72330526") {
        if (login) login(username);
        navigate("/admin");
        return;
      }

      setMsg("Login failed: check email/password or backend route");
    }
  };

  return (
    <div
      className="home-container"
      style={{
        backgroundImage: 'url(${loginBg})',  // ✅ fixed
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
      }}
    >
      <div className="login-container">
        <h2>Login</h2>

        {msg && <p style={{ color: "red" }}>{msg}</p>}

        <input
          placeholder="Username or Email"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <button onClick={handleLoginClick}>Login</button>

        <p>
          Don't have an account?{" "}
          <Link to="/signup">Sign Up</Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
