import React, { useState } from "react";
import axios from "axios";
import repairImg from "../assets/repair.jpg";
import "../App.css";

export default function Repair({ user }) {
  const BASE = "http://localhost:5000";

  const [phone, setPhone] = useState("");
  const [description, setDescription] = useState("");

  const [imageBase64, setImageBase64] = useState("");
  const [imageType, setImageType] = useState("");

  const handleFile = (file) => {
    if (!file) {
      setImageBase64("");
      setImageType("");
      return;
    }

    // ✅ size limit (avoid huge DB)
    if (file.size > 2 * 1024 * 1024) {
      alert("Image too large. Please choose under 2MB.");
      return;
    }

    setImageType(file.type);

    const reader = new FileReader();
    reader.onload = () => {
      const result = reader.result || ""; // data:image/...;base64,xxxx
      const commaIndex = result.indexOf(",");
      const base64Only = commaIndex >= 0 ? result.slice(commaIndex + 1) : "";
      setImageBase64(base64Only);
    };
    reader.readAsDataURL(file);
  };

  const submit = async (e) => {
    e.preventDefault();
    if (!phone || !description) return alert("Fill phone + description");

    try {
      const res = await axios.post('${BASE}/api/repairs', {
        from_email: user?.email || "",
        phone,
        description,
        image_base64: imageBase64 || null,
        image_type: imageType || null,
      });

      if (res.data?.success) {
        alert("Repair submitted ✅");
        setPhone("");
        setDescription("");
        setImageBase64("");
        setImageType("");
        const el = document.getElementById("repairFile");
        if (el) el.value = "";
      } else {
        alert(res.data?.message || "Failed");
      }
    } catch (err) {
      alert(err.response?.data?.message || "Backend error");
    }
  };

  return (
    <div className="two-col-page">
      <img className="side-image" src={repairImg} alt="repair" />
      <div className="form-area">
        <h2>Phone Repair</h2>

        <form onSubmit={submit}>
          <input
            className="basic-input"
            placeholder="Phone number"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />

          <textarea
            className="basic-textarea"
            placeholder="Describe the problem"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />

          <input
            id="repairFile"
            className="basic-input"
            type="file"
            accept="image/*"
            onChange={(e) => handleFile(e.target.files?.[0])}
          />

          <button className="basic-btn" type="submit">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
