import React, { useEffect, useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import Navbar from "./components/Navbar";
import Footer from "./components/Footer";

import Home from "./pages/Home";
import Shop from "./pages/Shop";
import Repair from "./pages/Repair";
import About from "./pages/About";
import Contact from "./pages/Contact";
import Cart from "./pages/Cart";
import Checkout from "./pages/Checkout";
import Login from "./pages/Login";
import Signup from "./pages/Signup";

import AdminLayout from "./admin/AdminLayout";
import Users from "./admin/Users";
import Orders from "./admin/Orders";
import Categories from "./admin/Categories";
import Messages from "./admin/Messages";
import Repairs from "./admin/Repairs";
import Admins from "./admin/Admins";

const ADMIN_EMAIL = "72330526@students.liu.edu.lb";
const ADMIN_PASS = "Rayan72330526";

export default function App() {
  const [user, setUser] = useState(null);

  // ✅ Ensure admin exists in localStorage users list
  useEffect(() => {
    const users = JSON.parse(localStorage.getItem("users") || "[]");
    const exists = users.some((u) => u.email === ADMIN_EMAIL);
    if (!exists) {
      users.push({
        name: "Main Admin",
        email: ADMIN_EMAIL,
        password: ADMIN_PASS,
        role: "admin",
      });
      localStorage.setItem("users", JSON.stringify(users));
    }
  }, []);

  // ✅ Load logged in user from localStorage
  useEffect(() => {
    const saved = localStorage.getItem("loggedUser");
    if (saved) setUser(JSON.parse(saved));
  }, []);

  // ✅ login function used by Login page
  const login = (loggedUserObj) => {
    setUser(loggedUserObj);
    localStorage.setItem("loggedUser", JSON.stringify(loggedUserObj));
  };

  // ✅ logout if you need later
  const logout = () => {
    setUser(null);
    localStorage.removeItem("loggedUser");
  };

  return (
    <>
      <Navbar user={user} logout={logout} />

      <Routes>
        {/* Public */}
        <Route path="/" element={<Home />} />
        <Route path="/shop" element={<Shop />} />
        <Route path="/about" element={<About />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/checkout" element={<Checkout />} />

        {/* Protected pages (must login) */}
        <Route path="/repair" element={<Repair user={user} />} />
        <Route path="/contact" element={<Contact user={user} />} />

        {/* Auth */}
        <Route path="/login" element={<Login login={login} />} />
        <Route path="/signup" element={<Signup />} />

        {/* Admin protected */}
        <Route
          path="/admin"
          element={
            user?.role === "admin" ? <AdminLayout /> : <Navigate to="/login" />
          }
        />
        <Route
          path="/admin/users"
          element={user?.role === "admin" ? <Users /> : <Navigate to="/login" />}
        />
        <Route
          path="/admin/orders"
          element={user?.role === "admin" ? <Orders /> : <Navigate to="/login" />}
        />
        <Route
          path="/admin/categories"
          element={user?.role === "admin" ? <Categories /> : <Navigate to="/login" />}
        />
        <Route
          path="/admin/messages"
          element={user?.role === "admin" ? <Messages /> : <Navigate to="/login" />}
        />
        <Route
          path="/admin/repairs"
          element={user?.role === "admin" ? <Repairs /> : <Navigate to="/login" />}
        />
        <Route
          path="/admin/admins"
          element={user?.role === "admin" ? <Admins /> : <Navigate to="/login" />}
        />

        {/* fallback */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>

      <Footer />
    </>
  );
}
