import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import loginBg from "../assets/login-bg.jpg";
import "../App.css";

export default function Login({ setUser }) {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setMsg("Please fill all fields");
      return;
    }

    try {
      setMsg("");

      // ✅ proxy makes this call go to http://localhost:5000
      const res = await axios.post("api/auth/login", { email, password });

      if (res.data?.success && res.data?.user) {
        localStorage.setItem("loggedUser", JSON.stringify(res.data.user));
        setUser(res.data.user);

        if (res.data.user.role === "admin") navigate("/admin");
        else navigate("/");
      } else {
        setMsg(res.data?.message || "Login failed");
      }
    } catch (err) {
      const real =
        err.response?.data?.message ||
        'Request failed with status code ${err.response?.status}' ||
        err.message;
      setMsg(real);
    }
  };

  return (
    <div className="auth-bg" style={{ backgroundImage: 'url(${loginBg})' }}>
      <form className="auth-card" onSubmit={handleLogin}>
        <h2 className="auth-title">Login</h2>

        {msg && <p className="auth-msg">{msg}</p>}

        <label className="auth-label">Email</label>
        <input
          className="auth-input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="email@example.com"
        />

        <label className="auth-label">Password</label>
        <input
          className="auth-input"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="password"
        />

        <button className="auth-btn" type="submit">
          Login
        </button>

        <p className="auth-white">
          Don’t have an account?{" "}
          <Link className="auth-link" to="/signup">
            Sign Up
          </Link>
        </p>
      </form>
    </div>
  );
}
