import React, { useEffect, useState } from "react";
import axios from "axios";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [categoryName, setCategoryName] = useState("");

  const [productData, setProductData] = useState({
    name: "",
    price: "",
    description: "",
    image: null,
    category_id: ""
  });

  const fetchCategories = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/categories");
      setCategories(res.data || []);
    } catch (err) {
      console.error(err);
      alert("Failed to load categories");
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const addCategory = async () => {
    if (!categoryName) {
      alert("Category name required");
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/categories", {
        name: categoryName
      });

      if (res.data.success) {
        setCategoryName("");
        fetchCategories();
      } else {
        alert(res.data.message || "Failed to add category");
      }
    } catch (err) {
      console.error(err);
      alert("Server error");
    }
  };

  const addProduct = async () => {
    const { name, price, description, image, category_id } = productData;

    if (!name || !price || !category_id) {
      alert("Name, price and category required");
      return;
    }

    const formData = new FormData();
    formData.append("name", name);
    formData.append("price", price);
    formData.append("description", description);
    if (image) formData.append("image", image);
    formData.append("category_id", category_id);

    try {
      const res = await axios.post("http://localhost:5000/api/products", formData);

      if (res.data.success) {
        alert("Product added");
        setProductData({ name: "", price: "", description: "", image: null, category_id: "" });
        const el = document.getElementById("productImageInput");
        if (el) el.value = "";
        fetchCategories();
      } else {
        alert(res.data.message || "Failed to add product");
      }
    } catch (err) {
      console.error(err);
      alert("Server error");
    }
  };

  const deleteProduct = async (id) => {
    if (!window.confirm("Are you sure you want to delete this product?")) return;

    try {
      const res = await axios.delete("http://localhost:5000/api/orders/" + id);
      if (res.data.success) fetchCategories();
      else alert(res.data.message || "Failed to delete product");
    } catch (err) {
      console.error(err);
      alert("Server error");
    }
  };

  return (
    <div>
      <h2>Categories & Products</h2>

      <div style={{ marginBottom: "20px" }}>
        <h3>Add Category</h3>
        <input
          type="text"
          placeholder="Category name"
          value={categoryName}
          onChange={(e) => setCategoryName(e.target.value)}
          style={{ marginRight: "10px", padding: "8px" }}
        />
        <button onClick={addCategory}>Add Category</button>
      </div>

      <hr />

      <div style={{ marginBottom: "20px" }}>
        <h3>Add Product</h3>

        <input
          type="text"
          placeholder="Product name"
          value={productData.name}
          onChange={(e) => setProductData({ ...productData, name: e.target.value })}
          style={{ display: "block", marginBottom: "10px", padding: "8px", width: "320px" }}
        />

        <input
          type="number"
          placeholder="Price"
          value={productData.price}
          onChange={(e) => setProductData({ ...productData, price: e.target.value })}
          style={{ display: "block", marginBottom: "10px", padding: "8px", width: "320px" }}
        />

        <textarea
          placeholder="Description"
          value={productData.description}
          onChange={(e) => setProductData({ ...productData, description: e.target.value })}
          rows="4"
          style={{ display: "block", marginBottom: "10px", padding: "8px", width: "320px" }}
        />

        <input
          id="productImageInput"
          type="file"
          onChange={(e) => setProductData({ ...productData, image: e.target.files[0] })}
          style={{ display: "block", marginBottom: "10px" }}
        />

        <select
          value={productData.category_id}
          onChange={(e) => setProductData({ ...productData, category_id: e.target.value })}
          style={{ display: "block", marginBottom: "10px", padding: "8px", width: "320px" }}
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat.id} value={cat.id}>
              {cat.name}
            </option>
          ))}
        </select>

        <button onClick={addProduct}>Add Product</button>
      </div>

      <hr />

      {categories.length === 0 ? (
        <p>No categories</p>
      ) : (
        categories.map((cat) => (
          <div key={cat.id} style={{ marginBottom: "20px" }}>
            <h3>{cat.name}</h3>

            {cat.products && cat.products.length > 0 ? (
              <ul>
                {cat.products.map((p) => (
                  <li key={p.id} style={{ marginBottom: "6px" }}>
                    {p.name} - ${p.price}{" "}
                    <button onClick={() => deleteProduct(p.id)}>Delete</button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No products</p>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default Categories;
