import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import loginBg from "../assets/login-bg.jpg";
import "../App.css";

export default function Login({ setUser }) {
  const navigate = useNavigate();
  const BASE = "http://localhost:5000";

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const handleLogin = async () => {
    if (!email || !password) {
      setMsg("Fill all fields");
      return;
    }

    try {
      setMsg("");

      const res = await axios.post('${BASE}/api/auth/login', { email, password });

      if (res.data?.success && res.data?.user) {
        localStorage.setItem("loggedUser", JSON.stringify(res.data.user));
        setUser(res.data.user);
        navigate(res.data.user.role === "admin" ? "/admin" : "/");
      } else {
        setMsg(res.data?.message || "Login failed");
      }
    } catch (err) {
      // ✅ show the real backend error
      const real =
        err.response?.data?.message ||
        err.message ||
        "Backend error";
      setMsg(real);
    }
  };

  return (
    <div className="auth-bg" style={{ backgroundImage: 'url(${loginBg})' }}>
      <div className="auth-card">
        <h2 className="auth-title">Login</h2>

        {msg && <p className="auth-msg">{msg}</p>}

        <label className="auth-label">Email</label>
        <input
          className="auth-input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="email@example.com"
        />

        <label className="auth-label">Password</label>
        <input
          className="auth-input"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="password"
        />

        <button className="auth-btn" onClick={handleLogin}>
          Login
        </button>

        <p className="auth-white">
          Don’t have an account?{" "}
          <Link to="/signup" className="auth-link">
            Sign Up
          </Link>
        </p>
      </div>
    </div>
  );
}
