import React, { useEffect, useState } from "react";
import axios from "axios";

function Admins() {
  const MAIN_ADMIN_USERNAME = "72330526@students.liu.edu.lb";

  const [admins, setAdmins] = useState([]);
  const [newUsername, setNewUsername] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const loggedAdminUsername = localStorage.getItem("adminUsername");
  const isMainAdmin = loggedAdminUsername === MAIN_ADMIN_USERNAME;

  const fetchAdmins = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admins");
      setAdmins(res.data || []);
    } catch (err) {
      console.log(err);
      alert("Failed to load admins");
    }
  };

  useEffect(() => {
    fetchAdmins();
  }, []);

  const addAdmin = async () => {
    if (!isMainAdmin) {
      alert("Only main admin can add admins");
      return;
    }

    if (!newUsername || !newPassword) {
      alert("All fields required");
      return;
    }

    if (newPassword.length < 8) {
      alert("Password must be at least 8 characters");
      return;
    }

    try {
      const res = await axios.post(
        "http://localhost:5000/api/admins",
        { username: newUsername, password: newPassword },
        { headers: { "x-admin-username": loggedAdminUsername } }
      );

      if (res.data && res.data.success) {
        setNewUsername("");
        setNewPassword("");
        fetchAdmins();
      } else {
        alert((res.data && res.data.message) || "Failed to add admin");
      }
    } catch (err) {
      console.log(err);
      alert("Server error");
    }
  };

  const deleteAdmin = async (id, username) => {
    if (!isMainAdmin) {
      alert("Only main admin can delete admins");
      return;
    }

    if (username === MAIN_ADMIN_USERNAME) {
      alert("You cannot delete the main admin");
      return;
    }

    const ok = window.confirm("Are you sure you want to delete this admin?");
    if (!ok) return;

    try {
      const res = await axios.delete("http://localhost:5000/api/admins/" + id, {
        headers: { "x-admin-username": loggedAdminUsername }
      });

      if (res.data && res.data.success) {
        fetchAdmins();
      } else {
        alert((res.data && res.data.message) || "Failed to delete admin");
      }
    } catch (err) {
      console.log(err);
      alert("Server error");
    }
  };

  return (
    <div>
      <h2>Admins</h2>

      {!isMainAdmin ? (
        <p style={{ color: "red" }}>Only main admin can add/delete admins.</p>
      ) : null}

      <div style={{ border: "1px solid #ccc", padding: "10px", borderRadius: "6px", marginBottom: "15px" }}>
        <h3>Add Admin</h3>

        <input
          type="email"
          placeholder="Admin email"
          value={newUsername}
          onChange={(e) => setNewUsername(e.target.value)}
          style={{ padding: "8px", marginRight: "10px", width: "240px" }}
        />

        <input
          type="password"
          placeholder="Password (min 8 chars)"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          style={{ padding: "8px", marginRight: "10px", width: "240px" }}
        />

        <button onClick={addAdmin} disabled={!isMainAdmin}>
          Add
        </button>
      </div>

      <table border="1" width="100%" cellPadding="10">
        <thead>
          <tr>
            <th>ID</th>
            <th>Username</th>
            <th>Delete</th>
          </tr>
        </thead>

        <tbody>
          {admins.length === 0 ? (
            <tr>
              <td colSpan="3" align="center">
                No admins found
              </td>
            </tr>
          ) : (
            admins.map(function (a) {
              return (
                <tr key={a.id}>
                  <td>{a.id}</td>
                  <td>{a.username}</td>
                  <td align="center">
                    <button
                      onClick={() => deleteAdmin(a.id, a.username)}
                      disabled={!isMainAdmin || a.username === MAIN_ADMIN_USERNAME}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Admins;
