import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import loginBg from "../assets/login-bg.jpg";
import "../App.css";

const ADMIN_EMAIL = "72330526@students.liu.edu.lb";
const ADMIN_PASS = "Rayan72330526";

function Login({ login }) {
  const [username, setUsername] = useState(""); // email
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const navigate = useNavigate();

  const handleLoginClick = () => {
    if (username.trim() === "" || password.trim() === "") {
      alert("Please fill all fields");
      return;
    }

    setMsg("");

    // ✅ Admin login
    if (username === ADMIN_EMAIL && password === ADMIN_PASS) {
      login({ email: ADMIN_EMAIL, role: "admin" });
      navigate("/admin");
      return;
    }

    // ✅ Normal users from localStorage
    const users = JSON.parse(localStorage.getItem("users") || "[]");
    const found = users.find(
      (u) => u.email === username && u.password === password
    );

    if (!found) {
      setMsg("Login failed: check email/password");
      return;
    }

    login({ email: found.email, role: found.role || "user" });
    navigate("/");
  };

  return (
    <div className="auth-bg" style={{ backgroundImage: 'url(${loginBg})' }}>
      <div className="login-container">
        <h2>Login</h2>

        {msg && <p style={{ color: "red" }}>{msg}</p>}

        <input
          placeholder="Username or Email"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <button onClick={handleLoginClick}>Login</button>

        <p>
          Don't have an account? <Link to="/signup">Sign Up</Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
