import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import signupBg from "../assets/signup-bg.jpg";
import "../App.css";

export default function Signup() {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [msg, setMsg] = useState("");

  const signup = async () => {
    setMsg("");
    if (!username || !email || !password || !confirm) return setMsg("Fill all fields");
    if (password !== confirm) return setMsg("Passwords do not match");

    try {
      const res = await axios.post(`${BASE}/api/auth/signup`, { username, email, password });
      if (res.data && res.data.success) {
        nav("/login");
      } else {
        setMsg("Signup failed");
      }
    } catch (err) {
      setMsg(err?.response?.data?.message || "Signup failed");
    }
  };

  return (
    <div className="auth-bg" style={{ backgroundImage: `url(${signupBg})` }}>
      <div className="auth-card">
        <h2 className="auth-title">Sign Up</h2>
        {msg && <p className="auth-msg">{msg}</p>}

        <input className="auth-input" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
        <input className="auth-input" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input className="auth-input" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
        <input className="auth-input" type="password" placeholder="Confirm Password" value={confirm} onChange={(e) => setConfirm(e.target.value)} />

        <button className="auth-btn" onClick={signup}>Create Account</button>

        <p className="auth-link">
          Already have an account? <Link to="/login">Login</Link>
        </p>
      </div>
    </div>
  );
}
