import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import contactImg from "../assets/contact.jpg";
import "../App.css";

export default function Contact({ user }) {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [msg, setMsg] = useState("");

  useEffect(() => {
    if (!user) nav("/login");
  }, [user, nav]);

  const submit = async () => {
    setMsg("");
    if (!name || !email || !message) return setMsg("Fill all fields");

    try {
      const res = await axios.post(`${BASE}/api/contact`, { name, email, message });
      if (res.data && res.data.success) {
        setMsg("Message submitted ✅");
        setName(""); setEmail(""); setMessage("");
      } else {
        setMsg("Failed");
      }
    } catch (err) {
      setMsg(err?.response?.data?.message || "Server error");
    }
  };

  return (
    <div className="two-col-page">
      <img className="side-img" src={contactImg} alt="contact" />
      <div className="form-box">
        <h2>Contact Us</h2>
        {msg && <p className="form-msg">{msg}</p>}

        <input className="form-input" placeholder="Your name" value={name} onChange={(e) => setName(e.target.value)} />
        <input className="form-input" placeholder="Your email" value={email} onChange={(e) => setEmail(e.target.value)} />

        <textarea className="form-textarea" placeholder="Send message" value={message} onChange={(e) => setMessage(e.target.value)} />

        <button className="form-btn" onClick={submit}>Submit</button>
      </div>
    </div>
  );
}
