import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import signupBg from "../assets/signup-bg.jpg";
import "../App.css";

export default function Signup() {
  const BASE = "http://localhost:5000";
  const navigate = useNavigate();

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [msg, setMsg] = useState("");

  const handleSignup = async () => {
    if (!name || !email || !password || !confirm) return setMsg("Fill all fields");
    if (password !== confirm) return setMsg("Passwords do not match");

    try {
      setMsg("");
      const res = await axios.post('${BASE}/api/auth/signup', { name, email, password });
      if (res.data?.success) {
        alert("Account created ✅");
        navigate("/login");
      } else {
        setMsg(res.data?.message || "Signup failed");
      }
    } catch (err) {
      setMsg(err.response?.data?.message || "Backend error");
    }
  };

  return (
    <div className="auth-bg" style={{ backgroundImage: 'url(${signupBg})' }}>
      <div className="auth-card">
        <h2 className="auth-title">Sign Up</h2>
        {msg && <p className="auth-msg">{msg}</p>}

        <label className="auth-label">Username</label>
        <input className="auth-input" value={name} onChange={(e) => setName(e.target.value)} />

        <label className="auth-label">Email</label>
        <input className="auth-input" value={email} onChange={(e) => setEmail(e.target.value)} />

        <label className="auth-label">Password</label>
        <input className="auth-input" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />

        <label className="auth-label">Confirm Password</label>
        <input className="auth-input" type="password" value={confirm} onChange={(e) => setConfirm(e.target.value)} />

        <button className="auth-btn" onClick={handleSignup}>Create Account</button>

        <p className="auth-white">
          Already have an account? <Link to="/login" className="auth-link">Login</Link>
        </p>
      </div>
    </div>
  );
}
