import React, { useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

export default function AdminLayout() {
  const navigate = useNavigate();

  useEffect(() => {
    const adminUsername = localStorage.getItem("adminUsername");
    if (!adminUsername) navigate("/login");
  }, [navigate]);

  const logout = () => {
    localStorage.removeItem("adminUsername");
    navigate("/login");
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      <div style={{ width: 240, background: "#1e1e2f", color: "white", padding: 20 }}>
        <h2 style={{ textAlign: "center", marginBottom: 20 }}>Admin Panel</h2>

        <div style={{ display: "flex", flexDirection: "column", gap: 10 }}>
          <Link style={{ color: "white", textDecoration: "none" }} to="orders">Orders</Link>
          <Link style={{ color: "white", textDecoration: "none" }} to="messages">Messages</Link>
          <Link style={{ color: "white", textDecoration: "none" }} to="repairs">Repairs</Link>
          <Link style={{ color: "white", textDecoration: "none" }} to="users">Users</Link>
          <Link style={{ color: "white", textDecoration: "none" }} to="admins">Admins</Link>
          <Link style={{ color: "white", textDecoration: "none" }} to="categories">Categories</Link>

          <button onClick={logout} style={{ marginTop: 20, padding: "8px 10px", cursor: "pointer" }}>
            Logout
          </button>
        </div>
      </div>

      <div style={{ flex: 1, padding: 20, background: "#f4f4f4" }}>
        <Outlet />
      </div>
    </div>
  );
}
