import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import loginBg from "../assets/login-bg.jpg";
import "../App.css";

export default function Login({ setUser }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const navigate = useNavigate();
  const BASE = "http://localhost:5000";

  const handleLoginClick = async () => {
    if (!email || !password) return alert("Fill all fields");

    try {
      setMsg("");
      const res = await axios.post('${BASE}/api/auth/login', { email, password });

      if (res.data?.success && res.data?.user) {
        const user = res.data.user;

        // store session only (not database data)
        localStorage.setItem("loggedUser", JSON.stringify(user));
        if (setUser) setUser(user);

        if (user.role === "admin") navigate("/admin");
        else navigate("/");
      } else {
        setMsg(res.data?.message || "Login failed");
      }
    } catch (err) {
      setMsg(err.response?.data?.message || "Login failed (backend error)");
    }
  };

  return (
    <div className="auth-bg" style={{ backgroundImage: 'url(${loginBg})' }}>
      <div className="login-container">
        <h2>Login</h2>
        {msg && <p style={{ color: "red" }}>{msg}</p>}

        <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />

        <button onClick={handleLoginClick}>Login</button>

        <p>
          Don't have an account? <Link to="/signup">Sign Up</Link>
        </p>
      </div>
    </div>
  );
}
