import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function ProductCard(props) {
  const product = props.product;
  const navigate = useNavigate();
  const [qty, setQty] = useState(1);

  const increaseQty = () => {
    setQty(qty + 1);
  };

  const decreaseQty = () => {
    if (qty > 1) setQty(qty - 1);
  };

  const addToCart = () => {
    const saved = localStorage.getItem("cart");
    let cart = [];

    if (saved) {
      try {
        cart = JSON.parse(saved);
        if (!Array.isArray(cart)) cart = [];
      } catch (e) {
        cart = [];
      }
    }

    const index = cart.findIndex(function (item) {
      return item.id === product.id;
    });

    if (index !== -1) {
      cart[index].qty = (cart[index].qty || 1) + qty;
    } else {
      cart.push({
        id: product.id,
        name: product.name,
        price: Number(product.price),
        qty: qty
      });
    }

    localStorage.setItem("cart", JSON.stringify(cart));
    alert("Added to cart");
    setQty(1);
  };

  const buyNow = () => {
    addToCart();
    navigate("/cart");
  };

  return (
    <div style={{ width: "220px", border: "1px solid #ddd", borderRadius: "8px", padding: "10px" }}>
      {product.image ? (
        <img
          src={"http://localhost:5000/uploads/" + product.image}
          alt={product.name}
          style={{ width: "100%", height: "140px", objectFit: "cover", borderRadius: "6px" }}
        />
      ) : (
        <div
          style={{
            width: "100%",
            height: "140px",
            background: "#f2f2f2",
            borderRadius: "6px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          No Image
        </div>
      )}

      <h4 style={{ margin: "10px 0 5px" }}>{product.name}</h4>
      <p>${product.price}</p>

      <div style={{ marginBottom: "10px" }}>
        <button onClick={decreaseQty}>-</button>
        <span style={{ margin: "0 10px" }}>{qty}</span>
        <button onClick={increaseQty}>+</button>
      </div>

      <button onClick={addToCart} style={{ width: "100%", marginBottom: "8px" }}>
        Add to Cart
      </button>

      <button onClick={buyNow} style={{ width: "100%" }}>
        Buy Now
      </button>
    </div>
  );
}

export default ProductCard;
