import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import signupBg from "../assets/signup-bg.jpg";
import "../App.css";

export default function Signup() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [msg, setMsg] = useState("");

  const navigate = useNavigate();
  const BASE = "http://localhost:5000";

  const handleSignupClick = async () => {
    if (!username || !email || !password || !confirm) {
      alert("Please fill all fields");
      return;
    }
    if (password !== confirm) {
      alert("Passwords do not match");
      return;
    }

    try {
      setMsg("");
      const res = await axios.post('${BASE}/api/auth/signup', {
        name: username,
        email,
        password,
      });

      if (res.data?.success) {
        alert("Account created! You can login now.");
        navigate("/login");
      } else {
        setMsg(res.data?.message || "Signup failed");
      }
    } catch (err) {
      setMsg(err.response?.data?.message || "Signup failed (backend error)");
    }
  };

  return (
    <div className="auth-bg" style={{ backgroundImage: 'url(${signupBg})' }}>
      <div className="signup-container">
        <h2>Sign Up</h2>
        {msg && <p style={{ color: "red" }}>{msg}</p>}

        <input placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
        <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
        <input type="password" placeholder="Confirm Password" value={confirm} onChange={(e) => setConfirm(e.target.value)} />

        <button onClick={handleSignupClick}>Create Account</button>
      </div>
    </div>
  );
}
