import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Admins() {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();
  const [admins, setAdmins] = useState([]);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const load = async () => {
    const res = await axios.get('${BASE}/api/users');
    setAdmins((res.data || []).filter(u => u.role === "admin"));
  };

  useEffect(() => { load(); }, []);

  const addAdmin = async () => {
    if (!name || !email || !password) return setMsg("Fill all fields");
    try {
      setMsg("");
      const res = await axios.post('${BASE}/api/users/add-admin', { name, email, password });
      if (res.data?.success) {
        setName(""); setEmail(""); setPassword("");
        await load();
      } else setMsg(res.data?.message || "Failed");
    } catch (err) {
      setMsg(err.response?.data?.message || "Backend error");
    }
  };

  return (
    <div>
      <button className="back-btn" onClick={() => nav("/admin")}>← Back to Admin</button>
      <h2>Admins</h2>
      {msg && <p style={{ color: "red" }}>{msg}</p>}

      <div className="admin-form-row">
        <input placeholder="Name" value={name} onChange={e=>setName(e.target.value)} />
        <input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
        <input placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} />
        <button onClick={addAdmin}>Add Admin</button>
      </div>

      <ul>
        {admins.map(a => <li key={a.id}>{a.name} — {a.email}</li>)}
      </ul>
    </div>
  );
}
