import React, { useState } from "react";
import axios from "axios";

const BASE = "http://localhost:5000";

export default function Contact() {
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");

  const handleSend = async (e) => {
    e.preventDefault();

    if (!phone || !message) {
      alert("Please fill all fields");
      return;
    }

    try {
      const res = await axios.post(`${BASE}/api/messages`, { phone, message });

      if (res.data && res.data.success) {
        alert("Message sent successfully");
        setPhone("");
        setMessage("");
      } else {
        alert((res.data && res.data.message) || "Failed to send message");
      }
    } catch (err) {
      console.log(err);
      alert("Server error");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Contact Us</h2>

      <form onSubmit={handleSend}>
        <input
          type="text"
          placeholder="Phone number (ex: 70123456)"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          style={{ display: "block", marginBottom: 10, padding: 10, width: 300 }}
        />

        <textarea
          placeholder="Write your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          rows="5"
          style={{ display: "block", marginBottom: 10, padding: 10, width: 300 }}
        />

        <button type="submit">Send</button>
      </form>
    </div>
  );
}
