import React, { useState } from "react";
import axios from "axios";
import contactImg from "../assets/contact.jpg";
import "../App.css";

export default function Contact({ user }) {
  const BASE = "http://localhost:5000";
  const [message, setMessage] = useState("");

  const submit = async (e) => {
    e.preventDefault();
    if (!message.trim()) return alert("Write a message");

    try {
      const res = await axios.post('${BASE}/api/messages', {
        from_email: user?.email || "",
        message: message.trim(),
      });

      if (res.data?.success) {
        alert("Message sent ✅");
        setMessage("");
      } else {
        alert(res.data?.message || "Failed");
      }
    } catch {
      alert("Backend error");
    }
  };

  return (
    <div className="two-col-page">
      <img className="side-image" src={contactImg} alt="contact" />
      <div className="form-area">
        <h2>Contact Us</h2>

        <form onSubmit={submit}>
          <textarea
            className="basic-textarea"
            placeholder="Send message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button className="basic-btn" type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}
