import React from "react";
import { useNavigate } from "react-router-dom";

export default function About() {
  const navigate = useNavigate();

  return (
    <div className="about-container">
      <div className="about-image"></div>

      <div className="about-content">
        <h1>About Our Mobile Shop</h1>

        <p>
          We provide high-quality mobile phones, accessories, and professional
          repair services. Our mission is to give customers fast service,
          trusted products, and reliable support.
        </p>

        <p>
          Whether you are buying a new phone, fixing a broken screen, or
          upgrading accessories, we are here to help you.
        </p>

        <button
          className="about-btn"
          onClick={() => navigate("/shop")}
        >
          Explore More
        </button>
      </div>
    </div>
  );
}
