import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import "../App.css";

export default function Shop() {
  const BASE = "http://localhost:5000";
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get('${BASE}/api/products').then((res) => setProducts(res.data || []));
  }, []);

  const grouped = useMemo(() => {
    const map = {};
    for (const p of products) {
      const cat = p.category || "Other";
      if (!map[cat]) map[cat] = [];
      map[cat].push(p);
    }
    return map;
  }, [products]);

  const cats = useMemo(() => Object.keys(grouped).sort((a, b) => a.localeCompare(b)), [grouped]);

  return (
    <div className="shop-page">
      <h2 className="shop-title">Shop</h2>

      {products.length === 0 ? (
        <p>No products yet. Add them from Admin â†’ Products.</p>
      ) : (
        cats.map((cat) => (
          <div key={cat} className="shop-category-section">
            <h3 className="shop-category-title">{cat}</h3>

            <div className="shop-products-grid">
              {grouped[cat].map((p) => (
                <div key={p.id} className="shop-product-card">
                  <div className="shop-product-name">{p.name}</div>
                  <div className="shop-product-price">${p.price}</div>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
}
