import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import "../App.css";

export default function Shop() {
  const BASE = "http://localhost:5000";
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const load = async () => {
      const c = await axios.get(`${BASE}/api/categories`);
      const p = await axios.get(`${BASE}/api/products`);
      setCategories(c.data || []);
      setProducts(p.data || []);
    };
    load();
  }, []);

  const byCat = useMemo(() => {
    const map = {};
    for (const p of products) {
      const k = String(p.category_id);
      if (!map[k]) map[k] = [];
      map[k].push(p);
    }
    return map;
  }, [products]);

  return (
    <div className="shop-page">
      {categories.length === 0 ? (
        <p>No categories found.</p>
      ) : (
        categories.map((cat) => (
          <div key={cat.id} className="category-block">
            <h2 className="category-title">{cat.name}</h2>

            <div className="products-grid">
              {(byCat[String(cat.id)] || []).map((p) => (
                <div key={p.id} className="product-card">
                  {p.image ? (
                    <img className="product-img" src={p.image} alt={p.name} />
                  ) : (
                    <div className="product-img placeholder">No Image</div>
                  )}
                  <h3>{p.name}</h3>
                  <p className="price">${p.price}</p>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
}
