import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Admins() {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();

  const [admins, setAdmins] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const user = JSON.parse(localStorage.getItem("mobileShopUser"));

  const load = async () => {
    const res = await axios.get(`${BASE}/api/admins`);
    setAdmins(res.data || []);
  };

  useEffect(() => {
    load();
  }, []);

  const addAdmin = async () => {
    if (!username || !password) return;

    await axios.post(`${BASE}/api/admin/add`, {
      username,
      password,
      email: user.email
    });

    setUsername("");
    setPassword("");
    load();
  };

  const deleteAdmin = async (id) => {
    if (!window.confirm("Delete this admin?")) return;

    await axios.delete(`${BASE}/api/admin/delete/${id}`, {
      data: { email: user.email }
    });

    load();
  };

  const isMainAdmin = user?.email === "72330526@students.liu.edu.lb";

  return (
    <div>
      <button onClick={() => nav("/admin")}>⬅ Back to Admin</button>
      <h2>Admins</h2>

      {isMainAdmin && (
        <div style={{ display: "flex", gap: 10, marginBottom: 15 }}>
          <input
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={addAdmin}>Add Admin</button>
        </div>
      )}

      {admins.map((a) => (
        <div
          key={a.id}
          style={{
            display: "flex",
            justifyContent: "space-between",
            padding: 8,
            borderBottom: "1px solid #ddd"
          }}
        >
          <span>{a.username}</span>

          {isMainAdmin && (
            <button onClick={() => deleteAdmin(a.id)}>❌ Delete</button>
          )}
        </div>
      ))}
    </div>
  );
}
