import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Repairs() {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();
  const [rows, setRows] = useState([]);

  useEffect(() => {
    axios.get(`${BASE}/api/admin/repairs`).then(res => setRows(res.data || []));
  }, []);

  return (
    <div>
      <button onClick={() => nav("/admin")}>⬅ Back to Admin</button>
      <h2>Repairs</h2>

      {rows.map(r => (
        <div key={r.id} style={{ border: "1px solid #ccc", padding: 10, marginBottom: 10 }}>
          <b>{r.name}</b> — {r.phone}
          <p>{r.issue}</p>
          <img src={r.image} alt="repair" width="140" />
        </div>
      ))}
    </div>
  );
}
