import React, { useEffect, useState } from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";

const Shop = () => {
  const [categories, setCategories] = useState([]);
  const [search, setSearch] = useState("");

  const fetchCategories = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/categories");
      setCategories(res.data || []);
    } catch (err) {
      console.error(err);
      alert("Failed to load products");
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  // Search only when 3+ characters
  const searchText = search.trim().toLowerCase();
  const enableSearch = searchText.length >= 3;

  const filteredCategories = categories.map((cat) => {
    const products = Array.isArray(cat.products) ? cat.products : [];

    const filteredProducts = enableSearch
      ? products.filter((p) => (p.name || "").toLowerCase().includes(searchText))
      : products;

    return { ...cat, products: filteredProducts };
  });

  const finalCategories = enableSearch
    ? filteredCategories.filter((cat) => cat.products.length > 0)
    : filteredCategories;

  return (
    <div>
      {/* Search bar beneath navbar area, right side */}
      <div style={{ display: "flex", justifyContent: "flex-end", padding: "10px 20px" }}>
        <input
          type="text"
          placeholder="Search product (type 3 letters)..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            width: "260px",
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc"
          }}
        />
      </div>

      <div style={{ padding: "0 20px 20px 20px" }}>
        {finalCategories.length === 0 ? (
          <p>{enableSearch ? "No products found." : "No categories/products found."}</p>
        ) : (
          finalCategories.map((cat) => (
            <div key={cat.id} style={{ marginBottom: "30px" }}>
              <h2 style={{ marginBottom: "15px" }}>{cat.name}</h2>

              <div style={{ display: "flex", flexWrap: "wrap", gap: "15px" }}>
                {cat.products.map((prod) => (
                  <ProductCard key={prod.id} product={prod} />
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Shop;
