import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Products() {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();

  const [cats, setCats] = useState([]);
  const [products, setProducts] = useState([]);

  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [msg, setMsg] = useState("");

  const load = async () => {
    const [cRes, pRes] = await Promise.all([
      axios.get('${BASE}/api/categories'),
      axios.get('${BASE}/api/products'),
    ]);
    setCats(cRes.data || []);
    setProducts(pRes.data || []);
  };

  useEffect(() => { load(); }, []);

  const add = async () => {
    const p = Number(price);
    if (!name.trim() || !price || !categoryId) return setMsg("Fill name, price, category");
    if (Number.isNaN(p) || p <= 0) return setMsg("Enter valid price");

    try {
      setMsg("");
      const res = await axios.post('${BASE}/api/products', {
        name: name.trim(),
        price: p,
        category_id: Number(categoryId),
      });
      if (res.data?.success) {
        setName(""); setPrice(""); setCategoryId("");
        await load();
      } else setMsg(res.data?.message || "Failed");
    } catch (err) {
      setMsg(err.response?.data?.message || "Backend error");
    }
  };

  return (
    <div>
      <button className="back-btn" onClick={() => nav("/admin")}>‚Üê Back to Admin</button>
      <h2>Products</h2>
      {msg && <p style={{ color: "red" }}>{msg}</p>}

      <div className="admin-form-row">
        <input placeholder="Product name" value={name} onChange={e=>setName(e.target.value)} />
        <input placeholder="Price" value={price} onChange={e=>setPrice(e.target.value)} />
        <select value={categoryId} onChange={e=>setCategoryId(e.target.value)}>
          <option value="">Select category</option>
          {cats.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
        </select>
        <button onClick={add}>Add Product</button>
      </div>

      <table border="1" cellPadding="10" width="100%">
        <thead><tr><th>Name</th><th>Category</th><th>Price</th></tr></thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.name}</td>
              <td>{p.category || "-"}</td>
              <td>${p.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
