import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const adminUsername = localStorage.getItem("adminUsername");

  const logout = () => {
    localStorage.removeItem("adminUsername");
    navigate("/login");
  };

  return (
    <div
      style={{
        background: "#1e1e2f",
        color: "white",
        padding: "12px 20px",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center"
      }}
    >
      <div style={{ display: "flex", gap: "14px", alignItems: "center" }}>
        <Link to="/shop" style={{ color: "white", textDecoration: "none", fontWeight: "bold" }}>
          Mobile Shop
        </Link>

        <Link to="/shop" style={{ color: "white", textDecoration: "none" }}>Shop</Link>
        <Link to="/cart" style={{ color: "white", textDecoration: "none" }}>Cart</Link>

        {/* ALWAYS visible */}
        <Link to="/repair" style={{ color: "white", textDecoration: "none" }}>Repair</Link>
        <Link to="/contact" style={{ color: "white", textDecoration: "none" }}>Contact</Link>
      </div>

      <div style={{ display: "flex", gap: "10px", alignItems: "center" }}>
        {!adminUsername ? (
          <>
            <Link to="/login" style={{ color: "white", textDecoration: "none" }}>Login</Link>
            <Link to="/signup" style={{ color: "white", textDecoration: "none" }}>Signup</Link>
          </>
        ) : (
          <>
            <Link to="/admin/orders" style={{ color: "white", textDecoration: "none" }}>Admin</Link>
            <button onClick={logout} style={{ padding: "6px 10px", cursor: "pointer" }}>
              Logout
            </button>
          </>
        )}
      </div>
    </div>
  );
}
