import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

export default function Repairs() {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();
  const [repairs, setRepairs] = useState([]);

  useEffect(() => {
    axios.get('${BASE}/api/repairs').then((res) => setRepairs(res.data || []));
  }, []);

  return (
    <div>
      <button className="back-btn" onClick={() => nav("/admin")}>
        ‚Üê Back to Admin
      </button>

      <h2>Repairs</h2>

      <table border="1" cellPadding="10" width="100%">
        <thead>
          <tr>
            <th>From</th>
            <th>Phone</th>
            <th>Description</th>
            <th>Image</th>
            <th>Date</th>
          </tr>
        </thead>

        <tbody>
          {repairs.map((r) => {
            const hasImg = r.image_base64 && r.image_type;
            const src = hasImg
              ? 'data:${r.image_type};base64,${r.image_base64}'
              : "";

            return (
              <tr key={r.id}>
                <td>{r.from_email}</td>
                <td>{r.phone}</td>
                <td>{r.description}</td>
                <td>
                  {hasImg ? (
                    <img
                      src={src}
                      alt="repair"
                      style={{
                        width: 110,
                        height: 110,
                        objectFit: "cover",
                        borderRadius: 8,
                      }}
                    />
                  ) : (
                    "-"
                  )}
                </td>
                <td>
                  {r.created_at ? new Date(r.created_at).toLocaleString() : "-"}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
