import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Categories() {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();
  const [name, setName] = useState("");
  const [categories, setCategories] = useState([]);
  const [msg, setMsg] = useState("");

  const load = async () => {
    const res = await axios.get('${BASE}/api/categories');
    setCategories(res.data || []);
  };

  useEffect(() => { load(); }, []);

  const add = async () => {
    if (!name.trim()) return setMsg("Enter category name");
    try {
      setMsg("");
      const res = await axios.post('${BASE}/api/categories', { name: name.trim() });
      if (res.data?.success) { setName(""); await load(); }
      else setMsg(res.data?.message || "Failed");
    } catch (err) {
      setMsg(err.response?.data?.message || "Backend error");
    }
  };

  return (
    <div>
      <button className="back-btn" onClick={() => nav("/admin")}>‚Üê Back to Admin</button>
      <h2>Categories</h2>
      {msg && <p style={{ color: "red" }}>{msg}</p>}

      <div className="admin-form-row">
        <input placeholder="Category name" value={name} onChange={e=>setName(e.target.value)} />
        <button onClick={add}>Add Category</button>
      </div>

      <ul>
        {categories.map(c => <li key={c.id}>{c.name}</li>)}
      </ul>
    </div>
  );
}
