import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import loginBg from "../assets/login-bg.jpg";
import "../App.css";

export default function Login({ onLogin }) {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const login = async () => {
    setMsg("");
    if (!email || !password) return setMsg("Fill all fields");

    try {
      const res = await axios.post(`${BASE}/api/auth/login`, { email, password });
      if (res.data && res.data.success) {
        onLogin(res.data.user);

        // go admin if admin credentials
        if (email === "72330526@students.liu.edu.lb" && password === "Rayan72330526") {
          nav("/admin");
        } else {
          nav("/");
        }
      } else {
        setMsg("Login failed");
      }
    } catch (err) {
      setMsg(err?.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="auth-bg" style={{ backgroundImage: `url(${loginBg})` }}>
      <div className="auth-card">
        <h2 className="auth-title">Login</h2>
        {msg && <p className="auth-msg">{msg}</p>}

        <input className="auth-input" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input className="auth-input" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />

        <button className="auth-btn" onClick={login}>Login</button>

        <p className="auth-link">
          Don't have an account? <Link to="/signup">Sign Up</Link>
        </p>
      </div>
    </div>
  );
}
