import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import repairImg from "../assets/repair.jpg";

export default function Repair({ user }) {
  const BASE = "http://localhost:5000";
  const nav = useNavigate();

  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [issue, setIssue] = useState("");

  const [preview, setPreview] = useState(null);
  const [imagePath, setImagePath] = useState("");

  useEffect(() => {
    if (!user) nav("/login");
  }, [user, nav]);

  const handleFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setPreview(URL.createObjectURL(file));

    // ðŸ”´ SIMULATION: static image
    setImagePath("/uploads/repair.jpg");
  };

  const submit = async () => {
    if (!name || !phone || !issue || !imagePath) {
      alert("Fill all fields and choose image");
      return;
    }

    await axios.post(`${BASE}/api/repair`, {
      name,
      phone,
      issue,
      image: imagePath
    });

    setName("");
    setPhone("");
    setIssue("");
    setPreview(null);
    setImagePath("");

    alert("Repair submitted");
  };

  return (
    <div className="two-col-page">
      <img src={repairImg} className="side-img" alt="repair" />

      <div className="form-box">
        <h2>Repair Request</h2>

        <input placeholder="Your name" value={name} onChange={(e) => setName(e.target.value)} />
        <input placeholder="Phone number" value={phone} onChange={(e) => setPhone(e.target.value)} />
        <textarea placeholder="Describe problem" value={issue} onChange={(e) => setIssue(e.target.value)} />

        <input type="file" accept="image/*" onChange={handleFile} />

        {preview && <img src={preview} alt="preview" style={{ width: "100%", marginTop: 10 }} />}

        <button onClick={submit}>Submit</button>
      </div>
    </div>
  );
}
